app.get(
  '/api/profile',
  auth,
  asyncHandler(async (req, res) => {
    const user = await findUserById(req.user.userId);
    
    if (!user) {
      throw notFoundError('User', req.user.userId);
    }
    
    res.json({
      user: {
        id: user.id,
        email: user.email,
        name: user.name
      }
    });
  })
);

